import React, { Component } from "react";
import { Link } from "react-router-dom";
import { ProductConsumer } from "../context";

export default class Modal extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const { id, title, img, price } = value.modalProduct;
          const { modalOpen, closeModal } = value;
          if (!modalOpen) {
            return null;
          } else {
            return (
              <div className="modal hide" tabIndex="-1" role="dialog">
                <div className="modal-dialog modal-md" role="document">
                  <div className="modal-content">
                    <div className="modal-body">
                      <img
                        src="https://sunlimetech.com/portfolio/sunlimeuikit/imgs/s.png"
                        className="modal-img"
                      />
                      <h3 className="pt-5 mb-0 text-secondary">
                        Thanks for your love and support!
                      </h3>
                      <p className="pb-3 text-muted">
                        <small>
                          Hope that you enjoy it well! Check also our site
                        </small>
                      </p>
                      <a
                        role="button"
                        className="btn btn-secondary text-white mb-3"
                        href="https://www.sunlimetech.com"
                        target="_blank"
                      >
                        www.sunlimetech.com
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            );
          }
        }}
      </ProductConsumer>
    );
  }
}
