import React, { Component } from "react";
import { Link } from "react-router-dom";
import { ProductConsumer } from "../context";

export default class Modal extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const { id, title, img, price } = value.modalProduct;
          const { modalOpen, closeModal } = value;
          if (!modalOpen) {
            return null;
          } else {
            return (
              <div
                className="modal fade text-center py-5 subscribeModal-lg show"
                id="subscribeModal"
                tabindex="-1"
                role="dialog"
                aria-labelledby="exampleModalLabel"
                aria-hidden="false"
              >
                <div className="modal-dialog modal-lg" role="documenst">
                  <div className="modal-content">
                    <div className="modal-body">
                      <div className="top-strip"></div>
                      <a
                        className="h2"
                        href="https://www.fiverr.com/sunlimetech/design-and-fix-your-bootstrap-4-issues"
                        target="_blank"
                      >
                        Sunlimetech
                      </a>
                      <h3 className="pt-5 mb-0 text-secondary">Newsletter</h3>
                      <p className="pb-1 text-muted">
                        <small>
                          Sign up to update with our latest news and products.
                        </small>
                      </p>

                      <p className="pb-1 text-muted">
                        <small>
                          Your email is safe with us. We won't spam.
                        </small>
                      </p>
                      <div className="bottom-strip"></div>
                    </div>
                  </div>
                </div>
              </div>
            );
          }
        }}
      </ProductConsumer>
    );
  }
}
