import React, { Component } from "react";
import { Link } from "react-router-dom";
import { ProductConsumer } from "../context";
import "./Modal.css";

export default class Modal extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const { id, title, img, price } = value.modalProduct;
          const { modalOpen, closeModal } = value;
          if (!modalOpen) {
            return null;
          } else {
            return (
              <div className="modal hide" tabIndex="-1" role="dialog">
                <div className="modal-dialog modal-md" role="document">
                  <div className="modal-content">
                    <div className="modal-body">
                      <div className="cd-quick-view">
                        <div className="cd-slider-wrapper">
                          <ul className="cd-slider">
                            <li className="selected">
                              <img src="img/item-1.jpg" alt="Product 1" />
                            </li>
                            <li>
                              <img src="img/item-2.jpg" alt="Product 2" />
                            </li>
                            <li>
                              <img src="img/item-3.jpg" alt="Product 3" />
                            </li>
                          </ul>

                          <ul className="cd-slider-navigation">
                            <li>
                              <a className="cd-next" href="#0">
                                Prev
                              </a>
                            </li>
                            <li>
                              <a className="cd-prev" href="#0">
                                Next
                              </a>
                            </li>
                          </ul>
                        </div>

                        <div className="cd-item-info">
                          <h2>Produt Title</h2>
                          <p>
                            Lorem ipsum dolor sit amet, consectetur adipisicing
                            elit. Officia, omnis illo iste ratione. Numquam
                            eveniet quo, ullam itaque expedita impedit. Eveniet,
                            asperiores amet iste repellendus similique
                            reiciendis, maxime laborum praesentium.
                          </p>

                          <ul className="cd-item-action">
                            <li>
                              <button className="add-to-cart">
                                Add to cart
                              </button>
                            </li>
                            <li>
                              <a href="#0">Learn more</a>
                            </li>
                          </ul>
                        </div>
                        <a href="#0" className="cd-close">
                          Close
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            );
          }
        }}
      </ProductConsumer>
    );
  }
}
